import { createWebHistory, createRouter } from "vue-router";
import { IS_USER_AUTHENTICATED } from "../store/storeconstants";

/*
const ifAuthenticated = (to, from, next) => {
  if (to.matched.some((record) => record.meta.requiresAuth)) {
    if (!store.getters["auth/IS_USER_AUTHENTICATED"]) {
      //next("/");
    } else {
      next("/dashboard");
    }
  } else {
    //next();
  }
};
*/

const routes = [
  {
    path: "/",
    name: "login",
    component: () => import("../components/LoginView.vue"),
  },
  {
    path: "/dashboard",
    name: "Dashboard",
    component: () => import("../components/DashboardView.vue"),
  },
  {
    path: "/marketplace",
    name: "Marketplace",
    component: () => import("../components/MarketplaceView.vue"),
    //beforeEnter: ifAuthenticated,
    meta: { requiresAuth: true },
  },
  {
    path: "/tournaments",
    name: "Tournaments",
    component: () => import("../components/TournamentsView.vue"),
    //beforeEnter: ifAuthenticated,
    meta: { requiresAuth: true },
  },
  {
    path: "/gamemanager",
    name: "GameManager",
    component: () => import("../components/GamemanagerView.vue"),
    //beforeEnter: ifAuthenticated,
    meta: { requiresAuth: true },
  },
  {
    path: "/configuration",
    name: "Configuration",
    component: () => import("../components/ConfigurationView.vue"),
    //beforeEnter: ifAuthenticated,
    meta: { requiresAuth: true },
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes: routes,
  linkActiveClass: "active",
});

router.beforeEach((to, from, next) => {
  // Acesse o estado de autenticação do Vuex
  const isAuthenticated = this.$store.getters[`auth/${IS_USER_AUTHENTICATED}`];

  console.log("isAuthenticated == ", isAuthenticated);
  // Verifique se a rota requer autenticação
  if (to.matched.some((record) => record.meta.requiresAuth)) {
    // Se a rota requer autenticação e o usuário não estiver autenticado, redirecione para a página de login
    if (!isAuthenticated) {
      next({
        path: "/",
        query: { redirect: to.fullPath }, // Salve o caminho para redirecionar de volta após o login
      });
    } else {
      // Se o usuário estiver autenticado, prossiga para a próxima rota
      next();
    }
  } else {
    // Se a rota não requer autenticação, prossiga para a próxima rota
    next();
  }
});

export default router;
