import { createWebHistory, createRouter } from "vue-router";
import { IS_USER_AUTHENTICATED } from "../store/storeconstants";
const ifAuthenticated = (to, from, next) => {
  console.log(IS_USER_AUTHENTICATED);
  const token = this.$store.getters[`auth/${GET_TOKEN}`];
            console.log("Valor atual do token:", token);
  if (this.$store.getters[`auth/${IS_USER_AUTHENTICATED}`]) {
    next();
    return;
  }
  this.$router.push("/");
};

const routes = [
  {
    path: "/",
    name: "login",
    component: () => import("../components/LoginView.vue"),
    beforeEnter: ifAuthenticated,
  },
  {
    path: "/dashboard",
    name: "Dashboard",
    component: () => import("../components/DashboardView.vue"),
    beforeEnter: ifAuthenticated,
  },
  {
    path: "/marketplace",
    name: "Marketplace",
    component: () => import("../components/MarketplaceView.vue"),
    beforeEnter: ifAuthenticated,
  },
  {
    path: "/tournaments",
    name: "Tournaments",
    component: () => import("../components/TournamentsView.vue"),
    beforeEnter: ifAuthenticated,
  },
  {
    path: "/gamemanager",
    name: "GameManager",
    component: () => import("../components/GamemanagerView.vue"),
    beforeEnter: ifAuthenticated,
  },
  {
    path: "/configuration",
    name: "Configuration",
    component: () => import("../components/ConfigurationView.vue"),
    beforeEnter: ifAuthenticated,
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes: routes,
  linkActiveClass: "active",
});

export default router;
