import { createWebHistory, createRouter } from "vue-router";
import store from "../store"; // Importe o store diretamente

/*
const ifAuthenticated = (to, from, next) => {
  if (to.matched.some((record) => record.meta.requiresAuth)) {
    if (!store.getters["auth/IS_USER_AUTHENTICATED"]) {
      //next("/");
    } else {
      next("/dashboard");
    }
  } else {
    //next();
  }
};
*/

const routes = [
  {
    path: "/",
    name: "login",
    component: () => import("../components/LoginView.vue"),
  },
  {
    path: "/dashboard",
    name: "Dashboard",
    component: () => import("../components/DashboardView.vue"),
    //beforeEnter: ifAuthenticated,

  },
  {
    path: "/marketplace",
    name: "Marketplace",
    component: () => import("../components/MarketplaceView.vue"),
    //beforeEnter: ifAuthenticated,
    meta: { requiresAuth: true },
  },
  {
    path: "/tournaments",
    name: "Tournaments",
    component: () => import("../components/TournamentsView.vue"),
    //beforeEnter: ifAuthenticated,
    meta: { requiresAuth: true },
  },
  {
    path: "/gamemanager",
    name: "GameManager",
    component: () => import("../components/GamemanagerView.vue"),
    //beforeEnter: ifAuthenticated,
    meta: { requiresAuth: true },
  },
  {
    path: "/configuration",
    name: "Configuration",
    component: () => import("../components/ConfigurationView.vue"),
    //beforeEnter: ifAuthenticated,
    meta: { requiresAuth: true },
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes: routes,
  linkActiveClass: "active",
});

router.beforeEach((to, from, next) => {
  // Verifique se a rota requer autenticação e se o usuário está autenticado
  if (to.matched.some((record) => record.meta.requiresAuth)) {
    if (!store.getters["auth/IS_USER_AUTHENTICATED"]) {
      // Se não estiver autenticado, redirecione para o login
      next({ name: "login" });
    } else {
      // Se estiver autenticado, permita a navegação
      next();
    }
  } else {
    // Para rotas que não requerem autenticação, permita a navegação
    next();
  }
});

export default router;
